import sys
line = ''
for elem in range(len(line)):
    if elem == 0:
        if line[elem].isupper() and line[elem+1].isupper() and line[elem+2].isupper():
            print(line[elem] ," : ", elem, end = " ")
    if elem>1 and elem<len(line)-2:
        if line[elem].isupper():
            if line[elem-2].islower() and line[elem-1].islower() and line[elem+1].isupper() and line[elem+2].isupper():
                print(line[elem] ," : ", elem, end = " ")
    if elem == len(line)-1:
        if line[elem].isupper() and line[elem-1].islower() and line[elem-2].islower():
            print(line[elem] ," : ", elem, end = " ")
    if elem == len(line):
        if line[elem].isupper() and line[elem-1].islower() and line[elem-2].islower():
            print(line[elem] ," : ", elem, end = " ")